# -*- coding:utf-8 -*-from django.contrib import adminfrom models import BingWallPaper, PhotoWall# Register your models here.class BingWPAdmin(admin.ModelAdmin):    list_display        = ('title','imgdate','imgurl','desc1','query1','desc2','query2','desc3','query3','desc4','query4','qiniuimgurl')    search_field        = ('imgdate',)    list_per_page       = 10class PhotoWallAdmin(admin.ModelAdmin):        list_display        = ('imgtype','created','image_display')    search_field        = ('imgtype')    list_per_page       = 10    def image_display(self, obj):                return '<img src="{0}?imageView2/1/w/150/h/96/q/80" width=150>'.format(obj.imgurl)    image_display.short_description = u'图片'    image_display.allow_tags = Trueadmin.site.register(BingWallPaper, BingWPAdmin)admin.site.register(PhotoWall, PhotoWallAdmin)